import React, { useEffect, useState } from "react";
import HeaderBar from "@components/header"
import { useHistory } from "react-router-dom";
import { queryBanks } from "./request";
import { observer, inject } from "mobx-react"
import "./style.scss"
import { Toast } from "antd-mobile";
enum Status {
 "WAIT_FOR_APPROVE" = "审批中",
 "ENABLE" = "启用",
 "DISABLE" = "禁用",
}
const Banks = (props: any) => {
 const { goBack, push } = useHistory();
 const { banksState } = props;
 const { mapBanks } = banksState;
 const [banks, setBanks] = useState<any>(null)
 useEffect(() => {
  queryBanksHandle()
 }, [])
 const queryBanksHandle = () => {
  Toast.loading("加载中...")
  queryBanks().then(data => {
   setBanks(data)
   Toast.hide();
  })
 }
 return (
  <div className="banks-wrap">
   <HeaderBar goback={goBack} title={"银行卡"} />
   <ul className="banks-container">
    {banks && banks.map((item: any, index: number) => {
     return (
      <li className="banks-item" key={index}>
       <span className="bank-icon"></span>
       <div className="back-right-wrap">
        {item.status === 'WAIT_FOR_APPROVE' && <p className="review-icon"></p>}
        <p className="right-top">
         <span>{mapBanks[item.bank]}</span>
         {
          item.status !== 'WAIT_FOR_APPROVE' && <span>删除</span>
         }
        </p>
        <p className="right-min">{item.cardNo}</p>
        <p className="right-bottom">
         <span>持卡人:{item.owner}</span>
         <span>{item.province} {item.city}</span>
        </p>
       </div>
      </li>
     )
    })}
   </ul>
   <div className="add-wrap" onClick={() => push('addBank')}>
    <span className="add-icon"></span>
    <span>添加银行卡</span>
   </div>
  </div >
 )
}
export default inject('banksState')((observer(Banks)));
