import { Checkbox, Radio, Toast } from "antd-mobile";
import React, { useEffect, useState } from "react";
import { saleList, receive, notReceive } from "../request"
import { mm_dd_hh_mm_ss2, yyyymmddhhmmss } from "@utils/dataTime"
import "../style.scss"
import { toFixed } from "@utils/numberUtils";
const OnSale = () => {
 const [oneAlertState, setOneAlertState] = useState(false)
 const [agentAlertState, setAgentAlertState] = useState(false)
 const [unMoneyState, setUnMoneyState] = useState(false)
 const [radioState, setRadioState] = useState(false)
 const [data, setData] = useState<any>(null)
 const [current, setCurrent] = useState<any>({})

 useEffect(() => {
  saleListHandle();
  const id = setInterval(() => {
   saleListHandle();
  }, 3000);
  return () => clearInterval(id);
 }, [])

 const saleListHandle = () => {
  saleList().then(data => {
   Toast.hide()
   setData(data)
  })
 }
 const receiveHandle = () => {
  Toast.loading("提交中...")
  receive({ saleItemId: current.orderId }).then(data => {
   setOneAlertState(false)
   setAgentAlertState(false)
   Toast.success("提交成功!")
  })
 }
 const notReceiveHandle = () => {
  Toast.loading("提交中...")
  notReceive({ saleItemId: current.orderId }).then(data => {
   setUnMoneyState(false)
   setRadioState(false)
   Toast.success("提交成功!")
  })
 }

 const oneAlert = () => {
  return (
   <div className="alert-container">
    <div className="on-sale-alert">
     <p className="alert-top">收款确认</p>
     <p className="alert-min">
      <span>请查看<i>【银行卡】</i>¥{toFixed(current.payAmount)}是否到账</span>
      <div className="item">
       <p className="item-bank">
        <span>银行卡号:{current.bankCardNo}</span>
        <span>付款人:{current.payerName}</span>
       </p>
       <p className="item-order">
        <span>订单编号:{current.orderId}</span>
        <span>{mm_dd_hh_mm_ss2(current.payTime)}</span>
       </p>
       <div className="item-info">
        <span className="order-icon"></span>
        <p className="order-i">
         <span className="special">¥{toFixed(current.payAmount)}</span>
         <span>赚:<i className="info-icon"></i>¥{toFixed(current.bonus)}</span>
        </p>
       </div>
      </div>
     </p>
     <p className="alert-bottom">
      <span onClick={() => setOneAlertState(false)}>取消</span>
      <span onClick={() => setAgentAlertState(true)}>已收到款</span>
     </p>
    </div>
   </div>
  )
 }

 const agentAlert = () => {
  return (
   <div className="alert-container">
    <div className="on-sale-alert">
     <p className="alert-top red-bg">收款确认</p>
     <p className="alert-min">
      <span>请再三确认,银行卡¥{toFixed(current.payAmount)}是否到账</span>
      <div className="item">
       <p className="item-bank red-font">
        若未收到款，点击确认收款,造成资金损失自行承担
       </p>
       <p className="item-order text-min">
        如有不确定的订单请联系客服处理
       </p>
      </div>
     </p>
     <p className="alert-bottom">
      <span onClick={() => {
       setOneAlertState(false)
       setAgentAlertState(false)
      }}>取消</span>
      <span onClick={receiveHandle}>再次确认</span>
     </p>
    </div>
   </div>
  )
 }

 const unMoneyAlert = () => {
  return (
   <div className="alert-container">
    <div className="on-sale-alert">
     <p className="alert-top">未收到款</p>
     <p className="alert-min">
      <span className="yellow-font">若已收到款项却点击【未收到款】,核实后可能会失去代售的机会</span>
      <div className="item">
       <p className="item-bank  just-start">
        <Checkbox onChange={(e) => { setRadioState(!radioState) }} /> 我已认真阅读以上提示,确定没有收到款
       </p>
      </div>
     </p>
     <p className="alert-bottom">
      <span className={radioState ? "" : "un-money"} onClick={notReceiveHandle}>未收到款</span>
      <span className={radioState ? "" : "un-money"} onClick={() => {
       setUnMoneyState(false)
       setRadioState(false)
      }}>取消</span>
     </p>
    </div>
   </div>
  )
 }

 const tiemAlert = () => {
  return (
   <div className="alert-container">
    <div className="on-sale-alert">
     <p className="alert-top">提示</p>
     <p className="alert-min">您有一笔订单已超时,请确认是否收款 并及时处理!</p>
     <p className="alert-bottom">
      <span>确定</span>
     </p>
    </div>
   </div>
  )
 }
 return (
  <>
   { oneAlertState && oneAlert()}
   { agentAlertState && agentAlert()}
   { unMoneyState && unMoneyAlert()}
   <ul className="consignments">
    {
     data && data.map((item: any) => {
      return (
       <li className="item" key={item.orderId}>
        <p className="item-bank">
         <span>银行卡号:{item.bankCardNo}</span>
         <span>付款人:{item.payerName}</span>
        </p>
        <p className="item-order">
         <span>订单编号:{item.orderId}</span>
         <span>{mm_dd_hh_mm_ss2(item.payTime)}</span>
        </p>
        <div className="item-info">
         <span className="order-icon"></span>
         <p className="order-i">
          <span className="special">¥{toFixed(item.payAmount)}</span>
          <span>赚:<i className="info-icon"></i>¥{toFixed(item.bonus)}</span>
         </p>
         <span className="off-money" onClick={() => {
          setCurrent(item)
          setUnMoneyState(true)
         }}>未收到款</span>
         <span className="on-money" onClick={() => {
          setCurrent(item)
          setOneAlertState(true)
         }}>已收到款</span>
        </div>
       </li>
      )
     })
    }

   </ul>
  </>
 )
}
export default OnSale;
