import React, { useEffect, useState } from "react";
import HeaderBar from "@components/header"
import { useHistory } from "react-router-dom";
import { Picker, List, Toast } from "antd-mobile"
import { statistics, queryPages } from "./request"
import './style.scss'
import { mm_dd_hh_mm_ss2 } from "@utils/dataTime";
const arr = [
   { code: 'TRANSFER_OUT', text: "转出" },
   { code: 'TRANSFER_IN', text: "代销扣款" },
   { code: 'DEPOSIT_REFUND', text: "代销退款" },
   { code: 'DEPOSIT_BONUS', text: "代销收益" },
   { code: 'DEPOSIT_RECALL', text: "代销追回" },
   { code: 'MANUAL_ADJUST', text: "额度修正" },
   { code: 'TIMING_TASK_REVOKE', text: "限时任务冲正" },
   { code: 'TIMING_TASK_INCOME', text: "限时任务奖励" },
   { code: 'TIMING_TASK_BONUS', text: "限时任务收益" },
]
const Status: any = {
   'TRANSFER_OUT': "转出",
   'TRANSFER_IN': "转入",
   'DEPOSIT_COST': "代销扣款",
   'DEPOSIT_REFUND': "代销退款",
   'DEPOSIT_BONUS': "代销收益",
   'DEPOSIT_RECALL': "代销追回",
   'MANUAL_ADJUST': "额度修正",
   'TIMING_TASK_REVOKE': "限时任务冲正",
   'TIMING_TASK_INCOME': "限时任务奖励",
   'TIMING_TASK_BONUS': "限时任务收益"
}
const Bills = () => {
   const { goBack } = useHistory();
   const [selector, setSelector] = useState(false)
   const [dataInfo, setDataInfo] = useState<any>({})
   const [dataList, setDataList] = useState<any>([])
   const [type, setType] = useState<any>()
   const statisticsHandle = () => {
      statistics({}).then(data => {
         setDataInfo(data)
      })
   }
   const queryPagesHandle = () => {
      queryPages({}).then(data => {
         setDataList(data)
      })
   }
   useEffect(() => {
      queryPagesHandle();
      statisticsHandle()
   }, [])

   return (
      <>
         {
            selector && <ul className="selects">
               {
                  arr.map((item: any) => <li onClick={() => {
                     setType(item.code)
                     setSelector(false)
                  }}>{item.text}</li>)
               }
            </ul>
         }
         <header className="bills-header-wrap">
            <ul className="container">
               <li className="goback" onClick={goBack}></li>
               <li className="title">账单</li>
               <li className="title small-title" onClick={() => setSelector(!selector)}></li>
            </ul>
         </header>
         <div className="bills-detail">
            <p>近7日个人代售收益 ¥{dataInfo.depositAmount}</p>
            <p>近7日Q币购买总额 ¥{dataInfo.withdrawAmount}</p>
         </div>
         <ul className="bills-list">
            {
               dataList && dataList.map((item: any) => {
                  return <li className="bills-list-item">
                     <p>
                        <span className="type-name">{Status[item.transReason]}</span>
                        {item.accountingAction === 'CREDIT' ?
                           <span className="type-money add-money">+{item.amount}</span>
                           :
                           <span className="type-money less-money">-{item.amount}</span>}
                     </p>
                     <p>
                        <span className="type-order">订单编号:{item.orderNo}</span>
                        <span className="type-time">{mm_dd_hh_mm_ss2(item.createdDate)}</span>
                     </p>
                  </li>
               })
            }
         </ul>
      </>
   )
}
export default Bills;
