import React, { useEffect, useState } from "react";
import { historyTasks } from "../request"
import { mm_dd_hh_mm_ss2 } from "@utils/dataTime";
import { status } from "./entity"
import './style.scss'
const OffTimer = () => {
   const [data, setData] = useState<any>([])
   const historyTasksHandle = () => {
      historyTasks({}).then(data => {
         setData(data)
      })
   }
   useEffect(() => {
      historyTasksHandle()
   }, [])
   const returnIconType = (status: string) => {
      if (['FAIL', 'REFUSED'].includes(status)) {
         return "fail"
      }
      if ('SUCCESS' === status) {
         return "success"
      }
      return "waiting"
   }
   return (
      <ul className="task-wrap">
         {
            data.map((item: any) => {
               return (
                  <li className="timers-item">
                     <p className="order-number">订单号:{item.taskId}</p>
                     <div className="task-bottom">
                        <div className="left">
                           <i className={item.bankType === 'ALIPAY' ? "ali-pay-icon" : "def-bank-icon"}></i>
                        </div>
                        <div className="min">
                           <span>¥{item.cashAmount}</span>
                           <span>任务奖励:<i></i>{item.taskBonus}</span>
                           <span>{mm_dd_hh_mm_ss2(item.userLockTimeout)}</span>
                        </div>
                        <div className="right">
                           <span className={returnIconType(item.status)}></span>
                           <span>{status[item.status]}</span>
                        </div>
                     </div>
                  </li>
               )
            })
         }
      </ul>

   )
}
export default OffTimer;
