import React, { useEffect, useState } from "react";
import HeaderBar from "@components/header"
import { useHistory } from "react-router-dom";
import { addBank } from "./request"
import { observer, inject } from "mobx-react"
//@ts-ignore
import lrz from "lrz"
import "./style.scss"
import { Toast } from "antd-mobile";
const Banks = (props: any) => {
  const { banksState } = props;
  const { banks } = banksState;
  const { goBack } = useHistory();
  const [imgUrl, setImgUrl] = useState("")
  const [imgData, setImgData] = useState<any>(null);
  const [dataInfo, setDataInfo] = useState<any>({})
  useEffect(() => {
    if(imgUrl){
      Toast.success('添加图片成功')
    }
  }, [imgUrl])
  const change = (e: any, key: string) => {
    setDataInfo({ ...dataInfo, [key]: e.target.value })
  }

  const onChange = (e: any) => {
    if (!e.target.files) return
    const file = e.target.files[0];
    Toast.loading('图片上传中',1111)
    if (window.FileReader) {
      var reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onloadend = function (e) {
        //@ts-ignore
        setImgData(e.target.result)
      };
      let data = new FormData();
      data.append("file", file, file.name) 
      debugger 
      setImgUrl(file)
    }
  }

  const submit = () => {
    const { bankCardNo, owner, province, city, bankType, branchName } = dataInfo;
    if (!bankCardNo || !owner || !province || !city || !bankType || !branchName) {
      Toast.info("需要填写备注")
      return;
    }
    if (!imgUrl) {
      Toast.info("请上传图片")
      return;
    }
    const ownerEncode = encodeURI(owner)
    const provinceEncode = encodeURI(province)
    const cityEncode = encodeURI(city)
    const branchNameEncode = encodeURI(branchName)
    let data = new FormData();
    lrz(imgUrl).then(function (rst:any) {
      // 处理成功会执行
      console.log(rst);
     debugger

     //@ts-ignore
          data.append("file", rst.file, imgUrl.name)
          Toast.loading("添加中...",60)
          addBank(data, {
            bankCardNo, owner: ownerEncode, province: provinceEncode,
            city: cityEncode, bankType, branchName: branchNameEncode
          }).then(data => {
            Toast.success("提交成功");
            goBack();
          }).catch(err=>{
            Toast.fail(err.response && err.response.data.message||'请求出错');
          })
        })
        .catch(function (err:any) {
            // 处理失败会执行
        })
        .always(function () {
            // 不管是成功失败，都会执行
        });

  }
  return (
    <div className="add-banks-wrap">
      <HeaderBar goback={goBack} title={"添加银行卡"} />
      <div className="add-container">
        <p className="item">
          <span className="item-text">银行卡号*</span>
          <input className="item-input" type="number" onChange={(e) => change(e, 'bankCardNo')} placeholder="请输入15-22位银行卡号(必填)" />
        </p>
        <p className="item">
          <span className="item-text">持卡人*</span>
          <input className="item-input" onChange={(e) => change(e, 'owner')} placeholder="请输入姓名(必填)" />
        </p>
        <p className="item">
          <span className="item-text">银行名称*</span>
          <select className="item-input" onChange={(e) => change(e, 'bankType')}  placeholder="请输入银行名称(必填)" >
            <option value="" style={{ display: "none" }}></option>
            {banks.map((b: any) => {
              return (
                <option key={b.code} value={b.code}>{b.name}</option>
              )
            })}
          </select>
        </p>
        <p className="item">
          <span className="item-text">开户省份*</span>
          <input className="item-input" onChange={(e) => change(e, 'province')} placeholder="请输入开户省份(必填)" />
        </p>
        <p className="item">
          <span className="item-text">开户城市*</span>
          <input className="item-input" onChange={(e) => change(e, 'city')} placeholder="请输入开户城市(必填)" />
        </p>
        <p className="item">
          <span className="item-text">支行名称*</span>
          <input className="item-input" onChange={(e) => change(e, 'branchName')} placeholder="请输入支行名称(必填)" />
        </p>
      </div>
      <div className="timer-update">
        <p className="vlidate"><span>*</span>请上传银行卡正面照片,确保卡号清晰可见</p>
        <p className="update-wrap">
          {
            imgData ?
              <span className="update-img">
                <img className="task-img" src={imgData} />
                <input type="file" accept="image/png, image/jpeg, image/gif, image/jpg" multiple={false} onChange={(e) => onChange(e)} />
              </span>
              :
              <span className="update-img">
                <input type="file" accept="image/png, image/jpeg, image/gif, image/jpg" multiple={false} onChange={(e) => onChange(e)} />
              </span>
          }
        </p>
      </div>
      <div className="pay-on-btn" onClick={() => submit()}>完成</div>
    </div>
  )
}
export default inject('banksState')((observer(Banks)));
