import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import HeaderBar from "@components/header"
import { save } from "./request"
import { Toast } from "antd-mobile";
import { observer, inject } from "mobx-react"
import "./style.scss"
const Register = (props: any) => {
 const { replace } = useHistory();
 const [registerInfo, setRegisterInfo] = useState<any>({verifyCodeId:1111})
 // 注册跳转
 const back = () => {
  replace("/login")
 }

 const change = (e: any, key: string) => {
  setRegisterInfo({ ...registerInfo, [key]: e.target.value })
 }
 const register = () => {
  const { loginName, entryPwd, pwd, realName, tel, activateCode, verifyCodeValue } = registerInfo;
  if (!loginName || !entryPwd || !pwd || !realName || !tel || !activateCode ) {
   Toast.fail("填写信息不能为空 ", 1.5);
   return
  }
  if (loginName.length < 5) {
   Toast.fail("5-11位大小写英文字母或数字", 1.5);
   return;
  }
  if (pwd !== entryPwd) {
   Toast.fail("确认密码不一致", 1.5);
   return
  }
  if (realName.length > 10) {
   Toast.fail("真实姓名位数错误", 1.5);
   return;
  }
  if (tel.length !== 11) {
   Toast.fail("手机号位数错误", 1.5);
   return;
  }
  Toast.loading('Loading...');
  save({...registerInfo }).then((res: any) => {
    Toast.success("注册成功!");
    back();
  })
 }
 return <div className="register">
  <HeaderBar goback={back} title={"注册"} />
  <div className="main">
   <p className="inputs">
    <i className="icons account"></i>
    <span>账号名称</span>
    <input type="text" onChange={(e) => { change(e, "loginName") }} placeholder="5-11位大小写英文字母或数字" />
   </p>
   <p className="inputs">
    <i className="icons pw"></i>
    <span>登录密码</span>
    <input type="password" onChange={(e) => { change(e, "pwd") }} placeholder="6-18位大小写字母+数字组合" />
   </p>
   <p className="inputs">
    <i className="icons pw"></i>
    <span>确认密码</span>
    <input type="password" onChange={(e) => { change(e, "entryPwd") }} placeholder="确认密码" />
   </p>
   <p className="inputs">
    <i className="icons account"></i>
    <span>真实姓名</span>
    <input type="text" onChange={(e) => { change(e, "realName") }} placeholder="2-10位汉字" />
   </p>
   <p className="inputs">
    <i className="icons phone"></i>
    <span>手机号码</span>
    <input type="number" onChange={(e) => { change(e, "tel") }} placeholder="11位可用手机号" />
   </p>
   <p className="inputs">
    <i className="icons key"></i>
    <span>激活口令</span>
    <input type="text" onChange={(e) => { change(e, "activateCode") }} placeholder="请输入五位激活口令" />
   </p>
   {/* <p className="inputs">
    <i className="icons code"></i>
    <span>验证码</span>
    <input type="text" onChange={(e) => { change(e, "verifyCodeValue") }} placeholder="请输入验证码" />
   </p> */}
   <p className="register-btn" onClick={register}>注册</p>
  </div>
 </div>
}
export default inject('registerState')((observer(Register)));
