import axios from "axios"
import { post, get, upload } from "src/request/api"

// 获取活动类型
export const queryActivityTypes = (params = {}) => {
 return get('/index/redlists/activityinfo/getActivityType', params)
}
// 申请活动奖励
export const addActivityapply = (data={}) => {
 return post('/index/redlists/activityapply/add', { data })
}

// 上传图片
export const uploadImg = (data: any) => {
 return upload('/index/redlists/file/upload', data)
}
