import React, { useEffect, useState } from "react";
import NavBar from "@components/navBar"
import { observer, inject } from "mobx-react"
import { Switch } from "antd-mobile"
import { useHistory } from "react-router-dom"
import "./style.scss"
type UserInfoProps = {
 userState: UserState
}
const UserInfo = (props: UserInfoProps) => {
 const { push } = useHistory();
 const { userState, homeState } = props as propsData;
 const { logout } = userState as UserState;
 const [checked, setChecked] = useState<boolean>(true)
 const { data } = homeState;
 const logoutHandl = () => {
  logout();
  push("/login")
 }
 useEffect(() => {
  let sound = localStorage.getItem('sound') || true;
  if (sound === 'false') {
   setChecked(false)
  } else {
   setChecked(true)
  }
 }, [])
 const changeChecked = (sound: boolean) => {
  setChecked(sound)
  localStorage.setItem("sound", sound + '')
 }
 return (
  <div className="userinfo">
   <div className="user-info-top">
    <p className="user-left"></p>
    <p className="user-right"></p>
    <p className="user-header-wrap">
     <span className="user-header"></span>
     <span>{data.realName}</span>
     <span>{data.loginName}</span>
    </p>
   </div>
   <ul className="user-setting">
    <li className="setting-item" onClick={() => push("banks")}>
     <p>
      <span className="bank-icon icon"></span>
      <span className="text">收款银行卡</span>
     </p>
     <span className="left-arrow"></span>
    </li>
    <li className="setting-item" onClick={() => push("bills")}>
     <p>
      <span className="account-icon icon"></span>
      <span className="text">账单</span>
     </p>
     <span className="left-arrow"></span>
    </li>
    {data.agencyType !== 'COMMON_USER' && <li className="setting-item" onClick={() => push("activation")}>
     <p>
      <span className="key-icon icon"></span>
      <span className="text">激活口令</span>
     </p>
     <span className="left-arrow"></span>
    </li>}
    <li className="setting-item" onClick={() => push("password")}>
     <p>
      <span className="pw-icon icon"></span>
      <span className="text">修改密码</span>
     </p>
     <span className="left-arrow"></span>
    </li>
    <li className="setting-item">
     <p>
      <span className="sound-icon icon"></span>
      <span className="text">消息铃声</span>
     </p>
     <Switch
      checked={checked}
      onChange={() => {
       changeChecked(!checked)
      }}
     />
    </li>
   </ul>
   <p className="logout" onClick={() => logoutHandl()}>退出登陆</p>
   <NavBar />
  </div>
 )
}
export default inject('userState', 'homeState')((observer(UserInfo)));
