import React, { useEffect, useState } from "react";
import HeaderBar from "@components/header"
import { useHistory } from "react-router-dom";
import { changePwd } from "./request"
import "./style.scss"
import { Toast } from "antd-mobile";
const Password = () => {
  const [info, setInfo] = useState<any>({})
  const { goBack, push } = useHistory();
  const change = (e: any, key: string) => {
    setInfo({ ...info, [key]: e.target.value })
  }
  const changePwdHandle = () => {

    const { oldPwd, newPwd, entryPwd } = info;
    if (!oldPwd || !newPwd || !entryPwd) {
      Toast.fail("您有选项未填写");
      return;
    }
    if (newPwd !== entryPwd) {
      Toast.fail("新密码与确认密码不一致");
      return;
    }
    Toast.loading("修改中...")
    changePwd({ oldPwd, newPwd }).then(data => {
      Toast.success("修改成功");
      push("userInfo");
    }).catch(err=>{
      Toast.success("修改失败");
    })
  }
  return (
    <div className="password-wrap">
      <HeaderBar goback={goBack} title={"修改密码"} />
      <div className="main">
        <p className="inputs">
          <i className="icons pw"></i>
          <span>原密码</span>
          <input type="password" onChange={(e) => { change(e, "oldPwd") }} placeholder="请输入原密码" />
        </p>
        <p className="inputs">
          <i className="icons pw"></i>
          <span>新密码</span>
          <input type="password" onChange={(e) => { change(e, "newPwd") }} placeholder="6-18位大小写字母+数字组合" />
        </p>
        <p className="inputs">
          <i className="icons pw"></i>
          <span>确认密码</span>
          <input type="password" onChange={(e) => { change(e, "entryPwd") }} placeholder="确认密码" />
        </p>
      </div>
      <div className="pay-on-btn" onClick={() => changePwdHandle()}>确认</div>
    </div>
  )
}
export default Password;
