import React, { useEffect, useState } from "react";
import NavBar from "@components/navBar"
import { observer, inject } from "mobx-react"
import { useHistory } from "react-router-dom";
import { myStatus } from "./request"
import './style.scss'
import { Toast } from "antd-mobile";
import { toFixed } from "@utils/numberUtils";
const Home = (props: any) => {
   const { push } = useHistory();
   const { homeState } = props;
   const [data, setData] = useState<any>({})
   const myStatusHandle = () => {
      myStatus({}).then(data => {
         setData(data)
         homeState.changeData(data)
      })
   }
   useEffect(() => {
      myStatusHandle()
   }, [])
   return (
      <div className="home">
         <div className="home-top"></div>
         <div className="home-mid">
            <div className="transf">
               <i className="transf-coin"></i>
               <div className="transf-detail">
                  <p className="qq-coin">
                     <span>Q币库存</span>
                     <i className="coins"></i>
                     <span>{toFixed(data.coinAmount)}</span>
                  </p>
                  <p>
                  </p>
               </div>
               <p className="transform-money" onClick={() => push("/transform")}>转账</p>
            </div>
            <div className="transf">
               <i className="got-coin"></i>
               <div className="got-detail">
                  <p className="qq-coin">
                     <span>今日收益</span>
                     <i className="coins"></i>
                     <span>{toFixed(data.todayIncome)}</span>
                  </p>
                  <p className="qq-coin">
                     <span>今日成交笔数：{data.todaySuccessOrderCount}</span>
                  </p>
               </div>
               <p className="transform-money display-none">转账</p>
            </div>
            <div className="discount">代售优惠:<i></i>卡转卡 {data.cardToCardPromotionRate && data.cardToCardPromotionRate * 100}%</div>
            <div className="pay-on-btn" onClick={() => push("consignment")}>代售</div>
         </div>
         <NavBar />
      </div>
   )
}
export default inject('homeState')((observer(Home)));
