import React, { useEffect, useState } from "react";
import NavBar from "@components/navBar"
import Header from "./component/header"
import Content from "./component/content"
import Tabs from "./component/tabs"
import { queryActivityTypes } from "./request"
import Activitys from "./component/activitys/activitys"
import "./style.scss"
const Activity = () => {
 const [type, setType] = useState(0);
 const [list, setList] = useState<any[]>([]);
 useEffect(() => {
  queryActivityTypes().then(res => {
   if (res.data && res.data.length > 0) {
    setType(res.data[0].id);
    setList(res.data);
   }
  })
 }, [])

 return (
  <div className="activity-wrap">
   <Header />
   <Tabs list={list} activity={type} setType={setType} />
   <div style={{ height:"100%", overflow:"auto", marginBottom: "1.6rem" }}>
    <Content type={type} />
    <Activitys type={type} />
   </div>
   <NavBar />
  </div>
 )
}
export default Activity;
