import React, { useState, useEffect } from "react"
import { Toast, Picker, List } from "antd-mobile"
import { observer, inject } from "mobx-react"
import { uploadImg, addActivityapply } from "../../request"
import "./style.scss"
type ContentProps = {
 type: number;
 [rondom: string]: any
}
const playTypes = [
 {
  label: '体育流水0.3%',
  value: 1,
 },
 {
  label: '真人流水0.1%',
  value: 2,
 },
];

const Content = (props: ContentProps) => {
 const { type, userState } = props;
 const [billFlowType, setBillFlowType] = useState<any>('');
 const [platformAccount, setPlatformAccount] = useState<string>('');
 const [level, setLevel] = useState<string>('');
 const [imgUrl, setImgUrl] = useState<string>('');
 const [imgName, setImgName] = useState<string>('');
 const { user } = userState as UserState;
 const onChange = (e: any) => {
  if (!e.target.files) return
  const file = e.target.files[0];
  let data = new FormData();
  data.append("file", file, file.name)
  Toast.loading("正在上传中...", 0)
  uploadImg(data).then(res => {
   const { data, code, msg } = res.data;
   if (code === 200) {
    setImgUrl(data.url)
    setImgName(file.name)
    Toast.success("上传成功")
   } else {
    Toast.fail(msg || "上传失败,请重新上传")
   }

  }).catch(err => {
   Toast.fail("上传失败,请重新上传")
  })
 }
 const add = () => {
  if (!user.isLogin) {
   Toast.fail("请先登录")
   return;
  }
  if (!platformAccount) {
   Toast.info("请输入平台账号")
   return;
  }
  if (type === 1) {
   if (!imgUrl) {
    Toast.info("请上传图片")
    return;
   }
  }
  if (type === 2) {
   if (!billFlowType) {
    Toast.info("请选择流水类型")
    return;
   }
  }
  if (type === 4) {
   if (!level) {
    Toast.info("请输入平台vip等级")
    return;
   }
   if (!imgUrl) {
    Toast.info("请上传图片")
    return;
   }
  }

  Toast.loading("提交申请中...")
  const data = { activityId: type, activityType: type, platformAccount, applyImg: imgUrl, level, billFlowType }
  addActivityapply(data).then(res => {
   Toast.success("提交成功!",)
   setPlatformAccount('')
   setImgUrl('')
   setBillFlowType('')
   setLevel('')
   setImgName('')
  })
 }

 useEffect(() => {
  setPlatformAccount('')
  setImgUrl('')
  setBillFlowType('')
  setLevel('')
  setImgName('')
 }, [type])

 const hasDone = () => {
  if (type === 1 && platformAccount && imgUrl) return true;
  if (type === 2 && platformAccount && billFlowType) return true;
  if (type === 3 && platformAccount) return true;
  if (type === 4 && platformAccount && level && imgUrl) return true;
  return false;
 }
 return (
  <div className="activi-content">
   <div className="account">
    <p className="name">平台账号</p>
    <p className="input">
     {/* @ts-ignore */}
     <input type="text" placeholder="请输入平台账号" value={platformAccount} onChange={(e) => setPlatformAccount(e.target.value)} />
    </p>
   </div>
   {
    type === 2 && <div className="account">
     <p className="name">流水类型</p>
     <Picker data={playTypes}
      cols={1}
      value={[billFlowType]}
      onChange={(v: any) => {
       setBillFlowType(v[0])
      }}
      onOk={v => setBillFlowType(v[0])}
      itemStyle={""}>
      <List.Item arrow="horizontal"> </List.Item>
     </Picker>
    </div>
   }
   {
    type === 4 && <div className="account">
     <p className="name">vip等级</p>
     <p className="input">
      {/* @ts-ignore */}
      <input type="text" placeholder="请输入vip等级" value={level} onChange={(e) => setLevel(e.target.value)} />
     </p>
    </div>
   }
   {[1, 4].includes(type) && <div className="upload">
    <p className="name">上传截图</p>
    <div className="bottom">
     <p className="text-content">
      <span className="icon-update"></span>
      <span className="text">
       <span>点击上传</span>
       <span>相册/拍照</span>
      </span>
      {imgName && <span className="img-name">{imgName}</span>}
     </p>
     <input type="file" accept="image/png, image/jpeg, image/gif, image/jpg" multiple={false} onChange={(e) => onChange(e)} />
    </div>
   </div>}
   <div className={`${hasDone() ? 'sumbit live' : 'sumbit'}`}>
    <p onClick={() => add()} >提交申请</p>
   </div>
  </div>
 )
}
export default inject('userState')((observer(Content)));
