import React, { useEffect, useState } from "react";
import HeaderBar from "@components/header"
import { useHistory } from "react-router-dom";
import { saleHistory } from "./request"
import { orderStatus } from "./entity"
import "./style.scss"
import { Toast } from "antd-mobile";
import { mm_dd_hh_mm_ss2 } from "@utils/dataTime";
const Activity = () => {
  const [data, setData] = useState<any>([])

  const { goBack } = useHistory();
  useEffect(() => {
    saleHistoryHandle()
  }, [])
  const saleHistoryHandle = () => {
    Toast.loading('加载中...')
    saleHistory().then(data => {
      setData(data)
      Toast.hide()
    })
  }
  return (
    <div className="consignment-record-wrap">
      <HeaderBar goback={goBack} title={"代售记录"} />
      {
        data.length > 0 ? <ul className="consignments">
          {
            data.map((item: any) => {
              return (
                <li className="item" key={item.orderId}>
                  <div className="item-info">
                    <div className="item-top">
                      <span className="order-icon"></span>
                      <p className="order-i">
                        <span className="special">¥{item.payAmount}</span>
                        <span>赚:<i className="info-icon"></i>¥{item.bonus}</span>
                      </p>
                    </div>
                    <span className="success">{orderStatus[item.orderStatus]}</span>
                  </div>
                  <p className="item-bank">
                    <span>银行卡号:{item.bankCardNo}</span>
                    <span>姓名:{item.payerName}</span>
                  </p>
                  <p className="item-order">
                    <span>订单编号:{item.orderId}</span>
                    <span style={{ flex: '1 1 30%', textAlign: 'end' }}>{mm_dd_hh_mm_ss2(item.payTime)}</span>
                  </p>
                </li>
              )
            })
          }
        </ul> : <div className="no-datas">暂无数据</div>
      }
    </div >
  )
}
export default Activity;
