import React, { useEffect, useState } from "react";
import HeaderBar from "@components/header"
import { useHistory } from "react-router-dom";
import { queryActivityTypes } from "./request"
import "./style.scss"
const Banks = () => {
  const { goBack, push } = useHistory();
  const [imgUrl, setImgUrl] = useState("")
  const [imgData, setImgData] = useState<any>(null);
  useEffect(() => {

  }, [])
  const onChange = (e: any) => {
    if (!e.target.files) return
    const file = e.target.files[0];
    if (window.FileReader) {
      var reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onloadend = function (e) {
        //@ts-ignore
        setImgData(e.target.result)
      };
      let data = new FormData();
      data.append("file", file, file.name)
      setImgUrl(file)
    }
  }
  return (
    <div className="add-banks-wrap">
      <HeaderBar goback={goBack} title={"添加银行卡"} />
      <div className="add-container">
        <p className="item">
          <span className="item-text">银行卡号*</span>
          <input className="item-input" placeholder="请输入15-22位银行卡号(必填)" />
        </p>
        <p className="item">
          <span className="item-text">持卡人*</span>
          <input className="item-input" placeholder="请输入姓名(必填)" />
        </p>
        <p className="item">
          <span className="item-text">银行名称*</span>
          <input className="item-input" placeholder="请输入银行名称(必填)" />
        </p>
        <p className="item">
          <span className="item-text">开户省份*</span>
          <input className="item-input" placeholder="请输入开户省份(必填)" />
        </p>
        <p className="item">
          <span className="item-text">开户城市*</span>
          <input className="item-input" placeholder="请输入开户城市(必填)" />
        </p>
        <p className="item">
          <span className="item-text">支行名称*</span>
          <input className="item-input" placeholder="请输入支行名称(必填)" />
        </p>
      </div>
      <div className="timer-update">
        <p className="vlidate"><span>*</span>请上传银行卡正面照片,确保卡号清晰可见</p>
        <p className="update-wrap">
          {
            imgData ?
              <span className="update-img">
                <img className="task-img" src={imgData} />
                <input type="file" accept="image/png, image/jpeg, image/gif, image/jpg" multiple={false} onChange={(e) => onChange(e)} />
              </span>
              :
              <span className="update-img">
                <input type="file" accept="image/png, image/jpeg, image/gif, image/jpg" multiple={false} onChange={(e) => onChange(e)} />
              </span>
          }
        </p>
      </div>
      <div className="pay-on-btn" onClick={() => push("consignment")}>完成</div>
    </div>
  )
}
export default Banks;
