import React, { useEffect, useState } from "react";
import { Toast } from "antd-mobile"
import { myTaskDetail, ackTimingTask,ackTiming } from "../request"
import { useHistory } from "react-router-dom";
import { status } from "./entity"
//@ts-ignore
import EXIF from "exif-js"

//@ts-ignore
import lrz from "lrz"
import { mm_dd_hh_mm_ss2, mm_ss_zh, seconds } from "@utils/dataTime";
import { copyArticle } from "@utils/helpers";
import { bankNumberHide, toFixed } from "@utils/numberUtils";
import '../style.scss'
import { forIn } from "lodash";
const TimerInfo = (props: any) => {
  const { push } = useHistory();
  const { mapBanks } = props;
  const [close, setClose] = useState(false);
  const [detail, setDetail] = useState<any>(null)
  const [imgData, setImgData] = useState<any>(null)
  const [imgData2, setImgData2] = useState<any>(null)
  const [imgData3, setImgData3] = useState<any>(null)
  const [second, setSecond] = useState(0)
  const [showAll, setShowAll] = useState(false)
  const [showName, setShowName] = useState(false)
  const offOrder = () => {
    setClose(false)
    push("/offTimer")
  }
  useEffect(() => {
    myTaskDetailHandle();
    const id = setInterval(() => {
      myTaskDetailHandle();
    }, 3000);
    return () => clearInterval(id)
  }, [])
  const myTaskDetailHandle = () => {
    myTaskDetail({}).then((data: any) => {
      setDetail(data)
      setSecond(seconds(data.userLockTimeout))
    })
  }
  useEffect(() => {
    let interval: any
    if (second >= 0) {
      interval = setInterval(() => {
        setSecond(preSecond => {
          if (preSecond <= 1) {
            clearInterval(interval);
            return 0
          } else {
            return preSecond - 1
          }
        })
      }, 1000)
    }
    return () => clearInterval(interval)
  }, [second])

  const closeAlert = () => {
    return (
      <div className="alert-container">
        <div className="on-sale-alert">
          <p className="alert-top">提示</p>
          <p className="alert-min">
            <div className="item font-40">
              取消后请勿继续打款,若发现恶意取消将限制接单,是否确定取消？
       </div>
          </p>
          <p className="alert-bottom">
            <span onClick={() => setClose(false)}>取消</span>
            <span onClick={offOrder}>确定</span>
          </p>
        </div>
      </div>
    )
  }
  const onChange = (e: any,index:number,cb:Function) => {
    if (!e.target.files) return
    const file = e.target.files[0];
    if(!file) return;
    let obj:any={}
    EXIF.getData(file, function() {
        obj =  EXIF.getAllTags(file);
    });
    let data = new FormData();
    lrz(file,{quality:0.1}).then(function (rst:any) {
      cb(rst.base64)
      //@ts-ignore
      data.append("file", rst.file, file.name)
      Toast.loading("上传中...",0)
      ackTimingTask(data, { taskId: detail.taskOrderId,appPackageNameEncoded:obj.UserComment,imgCreateTime:obj.Artist,imgIndex:index}).then(data => {
        Toast.success("上传成功");
      }).catch(err => {
        Toast.fail(err.response && err.response.data && err.response.data.message || '提交出错 ')
      })
    })
  }

  const submit = () => {
    if (imgData || imgData2|| imgData3) {
      Toast.loading("提交中...",0)
      ackTiming({ taskId: detail.taskOrderId }).then(data => {
          Toast.success("提交成功");
          myTaskDetailHandle();
      })
      return;
    }
    Toast.info("请上传照片",2)
  }
  return (
    <>
      {close && closeAlert()}
      {
        detail && detail.taskOrderId ? (
          <div className="timer-info">
            <div className="timer-info-top">
              <p>任务单号:{detail.orderNo}</p>
              <p>领取时间:{mm_dd_hh_mm_ss2(detail.takeTime)}</p>
              <p>任务奖励:<i></i>{detail.taskBonus}</p>
              <p className="down">任务倒计时:{mm_ss_zh(second)}</p>
              {/* @ts-ignore */}
              <div className="time-down">
                {status[detail.status]}
              </div>
            </div>
            <div className="alert-text">汇款时,所有信息必须复制,否则后果自负</div>
            {
              detail.bank !== 'ALIPAY' ? <div className="form-data">
                <p>
                  <span>卡号: {showAll ? detail.cardNo : bankNumberHide(detail.cardNo)} </span>
                  <span onClick={() => {
                    copyArticle(detail.cardNo)
                    setShowAll(true)
                  }}>查看复制</span>
                </p>
                <p>
                  {
                    showName ? <span>姓名:{detail.name}</span> : <span>姓名: {detail.name && detail.name.slice(0, 1)}* </span>
                  }
                  <span onClick={() => {
                    copyArticle(detail.name)
                    setShowName(true)
                  }}>查看复制</span>
                </p>
                <p>
                  <span>银行: {mapBanks[detail.bank]} </span>
                  <span onClick={() => copyArticle(mapBanks[detail.bank])}>查看复制</span>
                </p>
                <p>
                  <span>分行:  {detail.branch}  </span>
                  <span onClick={() => copyArticle(detail.branch)}>查看复制</span>
                </p>
                <p>
                  <span>金额: {toFixed(detail.amount)} </span>
                  <span onClick={() => copyArticle(detail.amount)}>查看复制</span>
                </p>
              </div> :
                <div className="form-data">
                  <p>
                    <span>支付宝账号: {showAll ? detail.cardNo : bankNumberHide(detail.cardNo)} </span>
                    <span onClick={() => {
                      copyArticle(detail.cardNo)
                      setShowAll(true)
                    }}>查看复制</span>
                  </p>
                  <p>
                    {
                      showName ? <span>支付宝姓名:{detail.name}</span> : <span>支付宝: {detail.name.slice(0, 1)}* </span>
                    }
                    <span onClick={() => {
                      copyArticle(detail.name)
                      setShowName(true)
                    }}>查看复制</span>
                  </p>
                  <p>
                    <span>金额: {toFixed(detail.amount)} </span>
                    <span onClick={() => copyArticle(detail.amount)}>查看复制</span>
                  </p>
                </div>
            }
            {
              detail.bank === 'ALIPAY' && <div className="form-data">
                <p>
                  <span>支付宝账号: {bankNumberHide(detail.cardNo)} </span>
                  <span onClick={() => copyArticle(detail.cardNo)}>查看复制</span>
                </p>
                <p>
                  <span>支付宝姓名: {detail.name.slice(0, 1)}* </span>
                  <span onClick={() => copyArticle(detail.name)}>查看复制</span>
                </p>
                <p>
                  <span>金额: {toFixed(detail.amount)} </span>
                  <span onClick={() => copyArticle(detail.amount)}>查看复制</span>
                </p>
              </div>
            }
            {console.log(['LOCKED', 'WAIT_PLATFORM_CHECK', 'SUBMIT_TIMEOUT'].includes(detail.status))}
            {
              ['LOCKED', 'WAIT_PLATFORM_CHECK', 'SUBMIT_TIMEOUT'].includes(detail.status) ? <></> :
                (
                  <>
                    <div className="timer-update">
                      <p className="vlidate"><span>*</span>上传凭证</p>
                      <p className="update-wrap">
                            <span className="update-img">
                              {imgData && <img className="task-img" src={imgData} />}
                              <input type="file" accept="image/png, image/jpeg, image/gif, image/jpg" multiple={false} onChange={(e) => onChange(e,1,setImgData)} />
                            </span>
                            <span className="update-img">
                              {imgData2 && <img className="task-img" src={imgData2} />}
                              <input type="file" accept="image/png, image/jpeg, image/gif, image/jpg" multiple={false} onChange={(e) => onChange(e,2,setImgData2)} />
                            </span>
                            <span className="update-img">
                              {imgData3 && <img className="task-img" src={imgData3} />}
                              <input type="file" accept="image/png, image/jpeg, image/gif, image/jpg" multiple={false} onChange={(e) => onChange(e,3,setImgData3)} />
                            </span>
                      </p>
                    </div>
                    <p className={imgData||imgData2||imgData3 ? "submit open" : "submit close"} onClick={() => submit()}>提交</p>
                    <p className="off-submit" onClick={() => setClose(true)} >取消付款</p>)
                  </>
                )
            }

            <div className="full"></div>
          </div>
        ) : (
            <div className="no-task-detail">暂无任务数据</div>
          )
      }

    </>

  )
}
export default TimerInfo;
