import React from "react";
import useServiceHooks from "@hooks/useServiceHooks"
type HeaderProps = {
 type: number;
 changeTab: (arg: any) => void;
}
const Header = (props: HeaderProps) => {
 let { onService } = useServiceHooks();
 const { changeTab, type } = props;
 return (
  <div className="header">
   <p>
    <span className={`activity ${type === 1 ? 'live' : ''}`} onClick={() => changeTab(1)}>消息</span>
   </p>
   {/* <p>
    <span className={`activity ${type === 2 ? 'live' : ''}`} onClick={() => changeTab(2)}>关注</span>
   </p> */}
   <span className="service" onClick={onService}></span>
  </div>
 )
}

export default Header;
