import React, { useState } from "react";
import { Toast } from "antd-mobile"
import { useHistory } from "react-router-dom";
import HeaderBar from "@components/header"
import { observer, inject } from "mobx-react"
import { login } from "./request"
import "./style.scss"
const Login = (props: any) => {
 const { push } = useHistory();
 const { userState } = props;
 const history = useHistory();
 const [nickname, setNickname] = useState<string>()
 const [password, setPassword] = useState<string>()
 let { updateUser } = userState as UserState;
 const toLogin = () => {
  if (!nickname || !password) {
   Toast.fail("请输入账号或密码",1111);
   return
  }
  Toast.loading('Loading...', 0)
  login({ nickname, password }).then((res: any) => {
   if (res.code === 200 && res.data) {
    const { sysUser } = res.data;
    updateUser({ ...sysUser, isLogin: true })
    Toast.hide()
    Toast.success("欢迎您的到来！");
    history.replace("/")
   }
  })
 }
 return (
  <div className="login">
   <HeaderBar goback={"/"} title="登录" />
   <div className="login-top">
    <p  onClick={()=>{push("/register")}}>注册账号</p>
   </div>
   <div className="main">
   <p className="inputs">
    <i className="icons account"></i>
    <span>账号名称</span>
    <input type="text"  placeholder="请输入账号或手机号" onChange={(e) => setNickname(e.target.value)}  />
   </p>
   <p className="inputs">
    <i className="icons pw"></i>
    <span>登录密码</span>
    <input type="password" placeholder="请输入密码" onChange={(e) => setPassword(e.target.value)} />
   </p>
   <p className="inputs">
    <i className="icons code"></i>
    <span>验证码</span>
    <input type="text" placeholder="请输入验证码" onChange={(e) => setPassword(e.target.value)} />
   </p>
    <p className="login-btn" onClick={toLogin}>登录</p>
   </div>
  </div>
 )
}
export default inject('userState')((observer(Login)));
