import React, { useEffect, useState } from "react";
import HeaderBar from "@components/header"
import { useHistory } from "react-router-dom";
import { observer, inject } from "mobx-react"
import { transferCion } from "./request"
import "./style.scss"
import { Toast } from "antd-mobile";
const Transform = (props: any) => {
  const { goBack, push } = useHistory();
  const { homeState } = props;
  const [transData, setTransData] = useState<any>({
    transferAmount:'',
    receiverLoginName:'',
    receiverName:'',
    loginPwd:'',
  });
  const { data, refreshData } = homeState

  const change = (e: any, key: string) => {
    setTransData({ ...transData, [key]: e.target.value })
  }

  const transferCionHandle = () => {
    Toast.loading("操作中...")
    transferCion(transData).then(res => {
      Toast.success("转账成功!");
      push("/")
    }).catch(err=>{
      setTransData({
        transferAmount:'',
        receiverLoginName:'',
        receiverName:'',
        loginPwd:'',
      })
    })
  }

  const submit = () => {
    const { transferAmount, receiverLoginName, receiverName, loginPwd } = transData;
    if (!transferAmount || !receiverLoginName || !receiverName || !loginPwd) {
      Toast.fail("填写信息不能为空 ", 1.5);
      return
    }
    if (transferAmount <= 0) {
      Toast.fail("转账金额不能小于0", 1.5);
      return;
    }
    if (transferAmount > data.coinAmount) {
      Toast.fail("账户余额不足", 1.5);
      return;
    }
    transferCionHandle()
  }
  return (
    <div className="transform-wrap">
      <HeaderBar goback={goBack} title={"转账"} />
      <div className="transform-top">
        <div className="transform-coin">
          <p className="coin-number">
            <i></i>
            <span>{data.coinAmount}</span>
          </p>
          <p className="coin-text">Q币余额</p>
        </div>
      </div>
      <div className="transf-form">
      <form method="post" action="/form" autoComplete="off">

        <p>
          <span>转账金额</span>
          <input type="number" placeholder="请输入转账金额" value={transData.transferAmount} onChange={(e) => { change(e, "transferAmount") }} />
        </p>
        <p>
          <span>对方账号</span>
          <input type="text" placeholder="请输入对方账号"  value={transData.receiverLoginName} onChange={(e) => { change(e, "receiverLoginName") }} />
        </p>
        <p>
          <span>对方姓名</span>
          <input type="text" autoComplete="off" placeholder="请输入对方姓名" value={transData.receiverName} onChange={(e) => { change(e, "receiverName") }} />
        </p>
        <p>
          <span>登陆密码</span>
          <input type="password" autoComplete="off" placeholder="请输入登陆密码" value={transData.loginPwd} onChange={(e) => { change(e, "loginPwd") }} />
        </p>
        </form>
      </div>

      <div className="transf-bottom">
        <p className="transf-alert">Q币将转入对方账号无法退款</p>
        <p className="transf-entry" onClick={() => submit()}>
          确定转账
    </p>
      </div>
    </div>
  )
}
export default inject('homeState')((observer(Transform)));
