import React, { useState, useEffect, useRef } from "react";
import NavBar from "@components/navBar"
import TimerList from "./component/list"
import TimerInfo from "./component/info"
import Tasks from "./component/task"
//@ts-ignore
import sound from "./img/order_done.mp3"
import { timingTasks } from "./request"
import { observer, inject } from "mobx-react"
import './style.scss'
import { checkSound } from "@utils/sound";
let tempData :any= [];
const TimeMission = (props: any) => {
   const [checkTab, setCheckTab] = useState(1);
   const [transType, setTransType] = useState(true);
   const { banksState } = props;
   const { mapBanks } = banksState;
   const soundControl = useRef<any>();
   const [data, setData] = useState<any>([])
   const timingTasksHandle = () => {
      timingTasks({}).then((res: any) => {
         soundControl.current && soundControl.current.pause()
         let newData: any = []
         let oldData: any = []
         res.map((item: any) => newData.push(item.taskId))
         tempData.map((item: any) => oldData.push(item.taskId))
         let isHas = newData.filter((v: any) => { return oldData.indexOf(v) == -1 })
         if (isHas.length > 0) {
            try {
               checkSound() && soundControl.current.play();
            } catch (error) {
            }
         }
         setData(res)
         tempData  = res;
      })
   }
   useEffect(() => {
      const id = setInterval(() => {
         timingTasksHandle();
      }, 3000);
      return () => clearInterval(id);
   }, [])
   useEffect(() => {
      if(checkTab===1){
         timingTasksHandle();
      }
   }, [checkTab])
   return (
      <div className="timeMission-wrap">
         <div className="header">
            <span className={checkTab === 1 ? "check" : ""} onClick={() => setCheckTab(1)}>任务列表</span>
            <span className={checkTab === 2 ? "check" : ""} onClick={() => setCheckTab(2)}>我的任务</span>
            <span className={checkTab === 3 ? "check" : ""} onClick={() => setCheckTab(3)}>任务记录</span>
         </div>
         <audio src={sound} ref={soundControl} preload="" />
         {checkTab === 1 && (
            <div className="transforms-wrap">
               <p className={transType ? "transform-check" : ""} onClick={() => setTransType(true)}>
                  <i className="bank-card-icon"></i>
                  <span>银行卡</span>
               </p>
               <p className={!transType ? "transform-check" : ""} onClick={() => setTransType(false)}>
                  <i className="ali-pay-icon"></i>
                  <span>支付宝</span>
               </p>
            </div>
         )}

         {checkTab === 1 && <TimerList transType={transType} data={data} mapBanks={mapBanks} setCheckTab={setCheckTab} />}
         {checkTab === 2 && <TimerInfo mapBanks={mapBanks} onClick={() => setCheckTab(3)} />}
         {checkTab === 3 && <Tasks />}
         <NavBar />
      </div>
   )
}
export default inject('homeState', 'banksState')((observer(TimeMission)));
