import { hh_mm_ss_zh } from "@utils/dataTime";
import { transferArrayToObj } from "@utils/helpers";
import React, { useState, useEffect } from "react";
import { timingTasks } from "../request"
import '../style.scss'
const TimerList = (props: any) => {
 const { setCheckTab, mapBanks, data, transType } = props;
 const [entryOrderFlag, setEntryOrderFlag] = useState(false)
 const [successOrderFlag, setSuccessOrderFlag] = useState(false)
 const [current, setCurrent] = useState<any>({})
 const entryOrder = () => {
  return (
   <div className="alert-container">
    <div className="on-sale-alert">
     <p className="alert-top">提示</p>
     <p className="alert-min">
      <span>领取后将扣除{current.cashAmount},是否领取</span>
      <div className="item">
       <div className="left">
        <i className={transType ? 'def-bank-icon' : 'ali-pay-icon'}></i>
        <span>{mapBanks[current.bankType]}</span>
       </div>
       <div className="min">
        <span>【万】¥{current.cashAmount}</span>
        <span>任务奖励:<i></i>{current.taskBonus}</span>
       </div>
       <div className="right">
        <span>领取倒计时</span>
        <span>{hh_mm_ss_zh(current.waitTimeout)}</span>
       </div>
      </div>
     </p>
     <p className="alert-bottom">
      <span onClick={() => {
       setEntryOrderFlag(false);
       setSuccessOrderFlag(true)
      }}>确定</span>
      <span onClick={() => setEntryOrderFlag(false)}>取消</span>
     </p>
    </div>
   </div>
  )
 }
 const successOrder = () => {
  return (
   <div className="alert-container">
    <div className="on-sale-alert">
     <p className="alert-top">提示</p>
     <p className="alert-min">
      <div className="item font-40">
       恭喜您，领取成功
      </div>
     </p>
     <p className="alert-bottom">
      <span onClick={() => {
       setSuccessOrderFlag(false)
       setCheckTab(2)
      }}>确定</span>
     </p>
    </div>
   </div>
  )
 }
 const aliPlayRender = () => {
  return (
   data && data.map((item: any) => {
    if (item.bankType !== 'ALIPAY') return
    return (
     <>
      {successOrderFlag && successOrder()}
      {entryOrderFlag && entryOrder()}
      <li className="timers-item">
       <div className="left">
        <i className="ali-pay-icon"></i>
        <span>{mapBanks[item.bankType]}</span>
       </div>
       <div className="min">
        <span>【万】¥{item.cashAmount}</span>
        <span>任务奖励:<i></i>{item.taskBonus}</span>
       </div>
       <div className="right">
        <span onClick={() => {
         setEntryOrderFlag(true)
         setCurrent(item)
        }}>领取</span>
        <span>领取倒计时</span>
        <span>{hh_mm_ss_zh(item.waitTimeout)} </span>
       </div>
      </li>
     </>
    )
   })
  )
 }
 const banksRender = () => {
  return (
   data && data.map((item: any) => {
    if (item.bankType === 'ALIPAY') return
    return (
     <>
      {successOrderFlag && successOrder()}
      {entryOrderFlag && entryOrder()}
      <li className="timers-item">
       <div className="left">
        <i className="def-bank-icon"></i>
        <span>{mapBanks[item.bankType]}</span>
       </div>
       <div className="min">
        <span>【万】¥{item.cashAmount}</span>
        <span>任务奖励:<i></i>{item.taskBonus}</span>
       </div>
       <div className="right">
        <span onClick={() => {
         setEntryOrderFlag(true);
         setCurrent(item)
        }}>领取</span>
        <span>领取倒计时</span>
        <span>{hh_mm_ss_zh(item.waitTimeout)} </span>
       </div>
      </li>
     </>
    )
   })
  )
 }
 return (
  <ul className="timers-wrap">
   {
    transType === true ? banksRender() : aliPlayRender()
   }
  </ul>
 )
}
export default TimerList;
