import React, { useEffect, useState } from "react";
import { Toast } from "antd-mobile"
import { myTaskDetail, uploadImg } from "../request"
import { useHistory } from "react-router-dom";
import '../style.scss'
const TimerInfo = () => {
  const { push, goBack } = useHistory();
  const [imgUrl, setImgUrl] = useState("");
  const [close, setClose] = useState(false);
  const [detail, setDetail] = useState<any>(null)
  const offOrder = () => {
    setClose(false)
    push("/offTimer")
  }
  useEffect(() => {
    myTaskDetail({}).then((data) => {
      setDetail(data)
    })
  }, [])
  const closeAlert = () => {
    return (
      <div className="alert-container">
        <div className="on-sale-alert">
          <p className="alert-top">提示</p>
          <p className="alert-min">
            <div className="item font-40">
              取消后请勿继续打款,若发现恶心取消将限制接单,是否确定取消？
       </div>
          </p>
          <p className="alert-bottom">
            <span onClick={offOrder}>确定</span>
            <span onClick={() => setClose(false)}>取消</span>
          </p>
        </div>
      </div>
    )
  }
  const onChange = (e: any) => {
    if (!e.target.files) return
    const file = e.target.files[0];
    let data = new FormData();
    data.append("file", file, file.name)
    Toast.loading("正在上传中...", 0)
    uploadImg(data).then((res: any) => {
      const { data, code, msg } = res.data;
      if (code === 200) {
        setImgUrl(data.url)
        Toast.success("上传成功")
      } else {
        Toast.fail(msg || "上传失败,请重新上传")
      }

    }).catch((err: any) => {
      Toast.fail("上传失败,请重新上传")
    })
  }
  return (
    <>
      {close && closeAlert()}
      {
        detail ? (
          <div className="timer-info">
            <div className="timer-info-top">
              <p>任务单号:1111111111111</p>
              <p>领取时间:2021-10-21 11:22:33</p>
              <p>任务奖励:<i></i>0.00</p>
              <p className="down">任务倒计时:10:22:33</p>
              <div className="time-down"> 未提交</div>
            </div>
            <div className="alert-text">汇款时,所有信息必须复制,否则后果自负</div>
            <div className="form-data">
              <p>
                <span>卡号: 6121 3231 3223  1212 </span>
                <span>查看复制</span>
              </p>
              <p>
                <span>姓名: 张三 </span>
                <span>查看复制</span>
              </p>
              <p>
                <span>银行: 中国邮政 </span>
                <span>查看复制</span>
              </p>
              <p>
                <span>分行: 北京市 </span>
                <span>查看复制</span>
              </p>
              <p>
                <span>金额: 62.00 </span>
                <span>查看复制</span>
              </p>
            </div>
            <div className="timer-update">
              <p className="vlidate"><span>*</span>上传凭证</p>
              <p>
                <span className="update-img">
                  <input type="file" accept="image/png, image/jpeg, image/gif, image/jpg" multiple={false} onChange={(e) => onChange(e)} />
                </span>
              </p>
            </div>
            <p className="submit">提交</p>
            <p className="off-submit" onClick={() => setClose(true)} >取消付款</p>
            <div className="full"></div>
          </div>
        ) : (
            <div className="no-task-detail">暂无任务详情数据</div>
          )
      }

    </>

  )
}
export default TimerInfo;
