import React from "react"
import { matchType, matchBallType } from "../../config"
import { yyyymmddhhmmss } from "@utils/dataTime"
import "./style.scss"

const Match = (props: MatchProps) => {
 const { data, changeCheck } = props;

 // 胜负平
 const click = (key: number) => {
  const copy: any = Object.assign({}, data);
  const { firstRecommend, secondRecommend } = copy;
  if (!firstRecommend) {
   copy.firstRecommend = key
   changeCheck(copy)
   return;
  }
  if (firstRecommend === key && secondRecommend) {
   return;
  }
  if (firstRecommend === key) {
   copy.firstRecommend = undefined
   changeCheck(copy)
   return;
  }
  if (secondRecommend === key) {
   copy.secondRecommend = undefined;
   changeCheck(copy)
   return;
  }
  copy.secondRecommend = key;
  changeCheck(copy)
 }

 // 大小球
 const clickBall = (key: number) => {
  const copy: any = Object.assign({}, data);
  const { sizeBallRecommend, secondSizeBallRecommend } = copy;
  changeCheck(copy)
  if (!sizeBallRecommend) {
   copy.sizeBallRecommend = key
   return;
  }
  if (sizeBallRecommend === key && secondSizeBallRecommend) {
   return;
  }
  if (sizeBallRecommend === key) {
   copy.sizeBallRecommend = undefined
   return;
  }
  if (secondSizeBallRecommend === key) {
   copy.secondSizeBallRecommend = undefined;
   return;
  }
  copy.secondSizeBallRecommend = key;
 }


 const getActivity = (data: MatchData, type: number) => {
  const { firstRecommend, secondRecommend } = data;
  if (firstRecommend === type) {
   return "main-activity"
  }
  if (secondRecommend === type) {
   return "minor-activity"
  }
  return ""
 }

 const getActivityBall = (data: MatchData, type: number) => {
  const { sizeBallRecommend, secondSizeBallRecommend } = data;
  if (sizeBallRecommend === type) {
   return "main-activity"
  }
  if (secondSizeBallRecommend === type) {
   return "minor-activity"
  }
  return ""
 }

 // 清空
 const clearClick = () => {
  const copy = Object.assign({}, data);
  copy.firstRecommend = undefined;
  copy.secondRecommend = undefined;
  copy.sizeBallRecommend = undefined;
  copy.secondSizeBallRecommend = undefined;
  changeCheck(copy)
 }
 return (
  <div className="push-match">
   <div className="left">
    <p className="home-name">
     {data.homeTeam}
    </p>
    <p className="match-info">
     <span>{data.matchName}</span>
     <span>{yyyymmddhhmmss(data.matchStartTime)}</span>
    </p>
   </div>
   <div className="center">
    <p className="vs">VS</p>
    <p className="win-loss">
     <span className={getActivity(data, matchType.homeWin)} onClick={() => click(matchType.homeWin)}>主胜</span>
     <span className={getActivity(data, matchType.draw)} onClick={() => click(matchType.draw)}>平局</span>
     <span className={getActivity(data, matchType.awayWin)} onClick={() => click(matchType.awayWin)}>客胜</span>
    </p>
    <p className="big-small">
     <span className={getActivityBall(data, matchBallType.big)} onClick={() => clickBall(matchBallType.big)}>大</span>
     <span className={getActivityBall(data, matchBallType.small)} onClick={() => clickBall(matchBallType.small)}>小</span>
     <span className={getActivityBall(data, matchBallType.walk)} onClick={() => clickBall(matchBallType.walk)}>走水</span>
    </p>
   </div>
   <div className="right">
    <p className="away-name">
     {data.guestTeam}
    </p>
    <p className="clear" onClick={clearClick}>清</p>
   </div>
  </div>
 )
}

export default Match;
