import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { statistics, queryPages } from "./request"
import { mm_dd_hh_mm_ss2 } from "@utils/dataTime";
import { toFixed } from "@utils/numberUtils";
import './style.scss'
const arr = [
   { code: 'TRANSFER_OUT', text: "转出" },
   { code: 'TRANSFER_IN', text: "转入" },
   { code: 'DEPOSIT_COST', text: "代收扣款" },
   { code: 'DEPOSIT_REFUND', text: "代收退款" },
   { code: 'DEPOSIT_BONUS', text: "代收收益" },
   { code: 'DEPOSIT_RECALL', text: "代销追回" },
   { code: 'MANUAL_ADJUST', text: "额度修正" },
   { code: 'TIMING_TASK_REVOKE', text: "限时任务冲正" },
   { code: 'TIMING_TASK_INCOME', text: "限时任务奖励" },
   { code: 'TIMING_TASK_BONUS', text: "限时任务收益" },
   { code: 'MERCHANT_DEPOSIT_FEE', text: "商户代收费用" },
   { code: 'MERCHANT_DEPOSIT_ADD', text: "商户代收费成功，额度增加" },
   { code: 'MERCHANT_TIMING_TASK_FEE', text: "商户限时任务费用" },
   { code: 'MERCHANT_TIMING_TASK_MINUS', text: "商户限时任务成功，额度减少" },
   { code: 'MERCHANT_TIMING_TASK_RETURN', text: "商户限时任务退回，额度补回" },
   { code: 'DEPOSIT_BONUS_TOP', text: '代收收益总代' },
   { code: 'DEPOSIT_BONUS_SECOND', text: '代收收益二级代理' },
   { code: 'DEPOSIT_BONUS_RECOMMEND1', text: '代收收益推荐人1' },
   { code: 'DEPOSIT_BONUS_RECOMMEND2', text: '代收收益推荐人2' },
   { code: 'DEPOSIT_BONUS_RECOMMEND3', text: '代收收益推荐人3' },
   { code: 'TIMING_TASK_BONUS_TOP', text: '限时任务收益总代' },
   { code: 'TIMING_TASK_BONUS_SECOND', text: '限时任务收益二级代理' },
   { code: 'TIMING_TASK_BONUS_RECOMMEND1', text: '限时任务收益推荐人1' },
   { code: 'TIMING_TASK_BONUS_RECOMMEND2', text: '限时任务收益推荐人2' },
   { code: 'TIMING_TASK_BONUS_RECOMMEND3', text: '限时任务收益推荐人3' },
]
const Status: any = {
   'TRANSFER_OUT': "转出",
  'TRANSFER_IN': "转入",
  'DEPOSIT_COST': "代收扣款",
  'DEPOSIT_REFUND': "代收退款",
  'DEPOSIT_BONUS': "代收收益",
  'DEPOSIT_RECALL': "代收追回",
  'MANUAL_ADJUST': "额度修正",
  'TIMING_TASK_REVOKE': "限时任务冲正",
  'TIMING_TASK_INCOME': "限时任务奖励",
  'TIMING_TASK_BONUS': "限时任务收益",
  'MERCHANT_DEPOSIT_FEE': "商户代收费用",
  'MERCHANT_DEPOSIT_ADD': "商户代收费成功，额度增加",
  'MERCHANT_TIMING_TASK_FEE': "商户限时任务费用",
  'MERCHANT_TIMING_TASK_MINUS': "商户限时任务成功，额度减少",
  'MERCHANT_TIMING_TASK_RETURN': "商户限时任务退回，额度补回",
  'DEPOSIT_BONUS_TOP': '代收收益总代',
  'DEPOSIT_BONUS_SECOND': '代收收益二级代理',
  'DEPOSIT_BONUS_RECOMMEND1': '代收收益推荐人1',
  'DEPOSIT_BONUS_RECOMMEND2': '代收收益推荐人2',
  'DEPOSIT_BONUS_RECOMMEND3': '代收收益推荐人3',
  'TIMING_TASK_BONUS_TOP': '限时任务收益总代',
  'TIMING_TASK_BONUS_SECOND': '限时任务收益二级代理',
  'TIMING_TASK_BONUS_RECOMMEND1': '限时任务收益推荐人1',
  'TIMING_TASK_BONUS_RECOMMEND2': '限时任务收益推荐人2',
  'TIMING_TASK_BONUS_RECOMMEND3': '限时任务收益推荐人3',
}
const Bills = () => {
   const { goBack } = useHistory();
   const [selector, setSelector] = useState(false)
   const [dataInfo, setDataInfo] = useState<any>({})
   const [dataList, setDataList] = useState<any>([])
   const [type, setType] = useState<any>()
   const statisticsHandle = () => {
      statistics({}).then(data => {
         setDataInfo(data)
      })
   }
   const queryPagesHandle = (data?: any) => {
      queryPages(data).then(data => {
         setDataList(data)
      })
   }
   useEffect(() => {
      queryPagesHandle();
      statisticsHandle()
   }, [])

   useEffect(() => {
      if (!type) return;
      queryPagesHandle({ transReasons: type })
   }, [type])
   return (
      <>
         {
            selector && <ul className="selects">
               {
                  arr.map((item: any) => <li onClick={() => {
                     setType(item.code)
                     setSelector(false)
                  }}>{item.text}</li>)
               }
            </ul>
         }
         <header className="bills-header-wrap">
            <ul className="container">
               <li className="goback" onClick={goBack}></li>
               <li className="title">账单</li>
               <li className="title small-title" onClick={() => setSelector(!selector)}></li>
            </ul>
         </header>
         <div className="bills-detail">
            <p>近7日个人代售收益 ¥{toFixed(dataInfo.depositAmount)}</p>
            <p>近7日Q币购买总额 ¥{toFixed(dataInfo.withdrawAmount)}</p>
         </div>
         <ul className="bills-list">
            {
               dataList && dataList.map((item: any) => {
                  return <li className="bills-list-item" key={item.orderNo}>
                     <p>
                        <span className="type-name">{Status[item.transReason]}</span>
                        {item.accountingAction === 'CREDIT' ?
                           <span className="type-money add-money">+{toFixed(item.amount)}</span>
                           :
                           <span className="type-money less-money">-{toFixed(item.amount)}</span>}
                     </p>
                     <p>
                        <span className="type-order">订单编号:{item.orderNo}</span>
                     </p>
                     <p>
                        <span className="type-time">订单日期:{mm_dd_hh_mm_ss2(item.createdDate)}</span>
                     </p>
                  </li>
               })
            }
         </ul>
      </>
   )
}
export default Bills;
