import React, { useEffect, useState } from "react";
import { Toast } from "antd-mobile"
import { myTaskDetail, ackTimingTask } from "../request"
import { useHistory } from "react-router-dom";
import { status } from "./entity"
import '../style.scss'
import { mm_ss_zh, seconds, yyyymmddhhmmss } from "@utils/dataTime";
import { copyArticle } from "@utils/helpers";
const TimerInfo = (props: any) => {
  const { push } = useHistory();
  const { mapBanks } = props;
  const [imgUrl, setImgUrl] = useState<any>(null);
  const [close, setClose] = useState(false);
  const [detail, setDetail] = useState<any>(null)
  const [second, setSecond] = useState(0)
  const offOrder = () => {
    setClose(false)
    push("/offTimer")
  }
  useEffect(() => {
    myTaskDetail({}).then((data: any) => {
      setDetail(data)
      setSecond(seconds(data.userLockTimeout))
    })
  }, [])

  useEffect(() => {
    let interval: any
    if (second) {
      interval = setInterval(() => {
        setSecond(preSecond => {
          if (preSecond <= 1) {
            clearInterval(interval)
            return 60
          } else {
            return preSecond - 1
          }
        })
      }, 1000)
    }
    return () => clearInterval(interval)
  }, [second])

  const closeAlert = () => {
    return (
      <div className="alert-container">
        <div className="on-sale-alert">
          <p className="alert-top">提示</p>
          <p className="alert-min">
            <div className="item font-40">
              取消后请勿继续打款,若发现恶心取消将限制接单,是否确定取消？
       </div>
          </p>
          <p className="alert-bottom">
            <span onClick={offOrder}>确定</span>
            <span onClick={() => setClose(false)}>取消</span>
          </p>
        </div>
      </div>
    )
  }
  const onChange = (e: any) => {
    if (!e.target.files) return
    const file = e.target.files[0];
    let data = new FormData();
    data.append("file", file, file.name)
    setImgUrl(file)
  }

  const submit = () => {
    if (!imgUrl) {
      Toast.info("请上传照片")
      return;
    }
    let data = new FormData();
    //@ts-ignore
    data.append("file", imgUrl, imgUrl.name)
    ackTimingTask(data, { taskId: detail.taskOrderId }).then(data => {
      Toast.success("提交成功");
    })
  }
  return (
    <>
      {close && closeAlert()}
      {
        detail ? (
          <div className="timer-info">
            <div className="timer-info-top">
              <p>任务单号:{detail.taskOrderId}</p>
              <p>领取时间:{yyyymmddhhmmss(detail.takeTime)}</p>
              <p>任务奖励:<i></i>{detail.taskBonus}</p>
              <p className="down">任务倒计时:{mm_ss_zh(second)}</p>
              {/* @ts-ignore */}
              <div className="time-down">{status[detail.status]}</div>
            </div>
            <div className="alert-text">汇款时,所有信息必须复制,否则后果自负</div>
            <div className="form-data">
              <p>
                <span>卡号: {detail.cardNo} </span>
                <span onClick={() => copyArticle(detail.cardNo)}>查看复制</span>
              </p>
              <p>
                <span>姓名:  {detail.name} </span>
                <span onClick={() => copyArticle(detail.name)}>查看复制</span>
              </p>
              <p>
                <span>银行: {mapBanks[detail.bank]} </span>
                <span onClick={() => copyArticle(mapBanks[detail.bank])}>查看复制</span>
              </p>
              <p>
                <span>分行:  {detail.branch}  </span>
                <span onClick={() => copyArticle(detail.branch)}>查看复制</span>
              </p>
              <p>
                <span>金额: {detail.amount} </span>
                <span onClick={() => copyArticle(detail.amount)}>查看复制</span>
              </p>
            </div>
            <div className="timer-update">
              <p className="vlidate"><span>*</span>上传凭证</p>
              <p>
                <span className="update-img">
                  <input type="file" accept="image/png, image/jpeg, image/gif, image/jpg" multiple={false} onChange={(e) => onChange(e)} />
                </span>
              </p>
            </div>
            <p className="submit" onClick={() => submit()}>提交</p>
            <p className="off-submit" onClick={() => setClose(true)} >取消付款</p>
            <div className="full"></div>
          </div>
        ) : (
            <div className="no-task-detail">暂无任务详情数据</div>
          )
      }

    </>

  )
}
export default TimerInfo;
