import React, { useEffect,Suspense } from 'react';
import { Switch, } from "react-router-dom"
import { observer, inject } from "mobx-react";
import AuthRoute from "./utils/authRoute"
import { ping } from "@pages/userInfo/request"
import { banks } from "@pages/banks/request"
import { myStatus } from "@pages/home/request"
import './App.scss';
import { Toast } from 'antd-mobile';
import { transferArrayToObj } from '@utils/helpers';
Toast.config({ duration: 1.3 })
const id = 0;
const App = (props: any) => {
  const { userState, banksState, homeState } = props;
  const { user } = userState;
  const { setBanks } = banksState;
  const { changeData } = homeState;
  const pings = () => {
    ping({}).then(() => { }).catch(err => {
    })
  }
  const myInfoHandle = () => {
    myStatus({}).then(data => {
      changeData(data)
    })
  }
  const banksStateHandle = () => {
    banks().then((data: any) => {
      if (data && data.length > 0) {
        let map = transferArrayToObj(data)
        setBanks(map, data)
      }
    })
  }
  useEffect(() => {
    if (user.isLogin) {
      myInfoHandle()
      banksStateHandle()
      const id = setInterval(() => {
        if (!user.isLogin) return;
        myInfoHandle()
        pings();
      }, 3000);
      return () => clearInterval(id);
    }
  }, [user])

  return (
    <>
      <Suspense fallback={<div>Loading...</div>}>
        <Switch>
          <AuthRoute></AuthRoute>
        </Switch>
      </Suspense>
    </>
  )
}

export default inject('userState', 'banksState', 'homeState')((observer(App)));
