import React from "react";
import "./style.scss"
type tabsProps = {
 check: number | undefined,
 change: (arg: any) => void;
 total: number,
 datas: any[]
}
const Tbas = (props: tabsProps) => {
 const { check, total, change, datas } = props;
 return (
  <div className="tabs">
   <ul className="content">
    {datas && datas.map((data, index) => {

     return (
      <li key={data.id} className={`${check === data.id ? 'activity' : ''}`}
       onClick={() => {
        change(data.id)
       }}>
       {(check === data.id && total > 0) && <span>{total}</span>}
       {data.name}
      </li>
     )
    })}
   </ul>
  </div>
 )
}

export default Tbas;
