import { Toast } from "antd-mobile";
import React, { useEffect, useState } from "react";
import { saleBankCard, saleBankCardStart, saleBankCardStop } from "../request"
import "../style.scss"
const status: any = {
 'WORKING': '停止代售',
 'IDLE': '开始代售'
}
const OnSale = (props: any) => {
 const [data, setData] = useState<any>([])
 const { banks } = props;

 useEffect(() => {
  saleBankCardHandle()
 }, [])

 const saleBankCardHandle = () => {
  Toast.loading("加载中")
  saleBankCard().then(data => {
   Toast.hide()
   setData(data)
  })
 }
 const onClick = (status: string, id: string) => {
  if (status == 'WORKING') {
   stopSale(id)
  } else {
   startSale(id)
  }
 }
 const startSale = (cardId: string) => {
  saleBankCardStart({ cardId }).then(data=>{
   Toast.success("开启成功");
   saleBankCardHandle();
  })
 }
 const stopSale = (cardId: string) => {
  saleBankCardStart({ cardId }).then(data=>{
   Toast.success("停止成功");
   saleBankCardHandle();
  })
 }
 return (
  <>
   <ul className="agent-sale">
    {data && data.map((item: any) => {
     return (
      <li className="item">
       <div className="item-bank">
        <span className="agent-sale-icon">{banks[item.bankType] ? banks[item.bankType].slice(0, 1) : ''}</span>
        <p className="agent-sale-info">
         <span>{banks[item.bankType]}:{item.cardNo}</span>
         <span>持卡人:{item.owner}</span>
        </p>
       </div>
       <div className="item-info">
        <p className="order-i">
         <span className="special">已接单笔数:{item.orderCount}</span>
        </p>
        <span className="on-money" onClick={() => onClick(item.status, item.bankCardId)}>{status[item.status]}</span>
       </div>
      </li>
     )
    })}
   </ul>
  </>
 )
}
export default OnSale;
