import React from "react"

type PublisProps = {
 submit: () => void
 ref: any
}
const Publish = React.forwardRef((props: PublisProps, ref: any) => {
 const { submit } = props;
 return (
  <div className="publish" ref={ref}>
   <div className="left">
    <p className="once">
     <span className="btn">主推</span>
     {/* <span className="text">点击1次</span> */}
    </p>
    <p className="double">
     <span className="btn">次推</span>
     {/* <span className="text">点击2次</span> */}
    </p>
    <p className="clear">
     <span className="btn">清</span>
     {/* <span className="text">清空选中</span> */}
    </p>
   </div>
   <div className="right">
    <span onClick={submit}>发布</span>
   </div>
  </div>
 )
})
export default Publish;
