 }
 @observable data = {};

 @action
 changeData = (val: any) => {
  debugger
  this.data = val;
 }
 @action
 refreshData = () => {
  myStatus({}).then(data=>{
   this.changeData(data)
  })
 }
}
export default HomeState;
