import React from "react"
import { updateToTop } from "../../request"
import { yyyymmddhhmmss2 } from "@utils/dataTime"
import { matchType, matchBallType } from "@entity/matchType"
import { Toast } from "antd-mobile"
type Item = {
 data: any;
 onRefresh: () => void
 isTop: boolean
}
const RenderItem = (props: Item) => {
 const { data, onRefresh, isTop } = props;

 const setTop = (id: number) => {
  updateToTop({ data: { id } }).then(res => {
   Toast.success("置顶成功", 1)
   onRefresh()
  })
 }

 const chooseType = (position: number, frist: string, second: string) => {
  if (position.toString() === frist) return "main-activity"
  if (position.toString() === second) return "minor-activity"
  return 'unactivity'
 }
 return (
  <div className="match">
   <p className="left">
    <span className="match-name">{data.matchName}</span>
    <span className="home-team">{data.homeTeam}</span>
    <span className="away-team">{data.guestTeam}</span>
    <span className="date-time">{yyyymmddhhmmss2(data.matchStartTime)} = {data.id}</span>
    {data.hitStatus && <span className="win-icon"></span>}
   </p>
   <div className="center">
    <p className="match-type">
     <span className={`${chooseType(matchType.homeWin, data.firstRecommend, data.secondRecommend)}`}>主胜</span>
     <span className={`${chooseType(matchType.draw, data.firstRecommend, data.secondRecommend)}`}>平局</span>
     <span className={`${chooseType(matchType.awayWin, data.firstRecommend, data.secondRecommend)}`}>客胜</span>
    </p>
    <p className="match-type match-type-b">
     <span className={`${chooseType(matchBallType.big, data.sizeBallRecommend, data.secondSizeBallRecommend)}`}>大</span>
     <span className={`${chooseType(matchBallType.small, data.sizeBallRecommend, data.secondSizeBallRecommend)}`}>小</span>
     <span className={`${chooseType(matchBallType.walk, data.sizeBallRecommend, data.secondSizeBallRecommend)}`}>走水</span>
    </p>
   </div>
   <p className="right">
    <span className={`${!isTop ? 'top-icon' : ''}`} onClick={() => setTop(data.id)}></span>
   </p>
  </div>
 )
}
export default RenderItem;
