import React, { useEffect, useState } from "react";
import HeaderBar from "@components/header"
import { useHistory } from "react-router-dom";
import { activationCodes } from "./request"
import "./style.scss"
import { copyArticle } from "@utils/helpers";
const Activation = () => {
  const [check, setCheck] = useState(1)
  const { goBack } = useHistory();
  const [activationList, setActivationList] = useState<any>([])
  useEffect(() => {
    activationCodesHandle()
  }, [])
  const activationCodesHandle = () => {
    activationCodes({}).then(data => {
      setActivationList(data)
    })
  }
  return (
    <div className="activation-wrap">
      <HeaderBar goback={goBack} title={"激活口令"} />
      <div className="activation-tabs">
        <p className={check === 1 ? "activation-a check" : "activation-a"} onClick={() => setCheck(1)}>已激活</p>
        <p className={check === 2 ? "activation-b check" : "activation-b"} onClick={() => setCheck(2)} >未激活</p>
      </div>
      {
        check == 1 && <ul className="activation-a-list">
          {
            activationList.length > 0 && activationList.map((item: any) => {
              if (!item.loginName) return;
              return (
                <li>
                  <span>{item.activationCode}</span>
                  <span>激活账号:{item.loginName}</span>
                </li>
              )
            })
          }
        </ul>
      }
      {
        check == 2 && <ul className="activation-a-list copy-wrap">
          {
            activationList.length > 0 && activationList.map((item: any) => {
              if (item.loginName) return;
              return (
                <li  key={item.activationCode}>
                  <span>{item.activationCode}</span>
                  <span className="copy" onClick={() => copyArticle(item.activationCode)}>复制</span>
                </li>
              )
            })
          }
        </ul>
      }
    </div >
  )
}
export default Activation;
